  posts_table = {}
  for p in posts() do
    table.insert(posts_table, p)
  end

  table.sort(posts_table, orderPosts)

  function replace(str, old, new)
    return (str:gsub(old, new))
  end
  -- https://www.w3.org/TR/NOTE-datetime
  -- The format needs to be YYYY-MM-DD
%}
